<script>
import inView from 'in-view';

export default {
  mounted() {
    ["one", "two", "three", "four", "five", "six"].forEach(
        item => this.handleView(item)
    );
  },
  methods: {
    handleView(item) {
      const linkEl = document.querySelector(`#link-${item}`);
      let offsetHeight = 0.6 * (window.innerHeight)
      inView.offset({
        bottom: offsetHeight
      });

      inView(`#${item}`)
      .on("enter", () => linkEl.classList.add('is-active'))
      .on("exit", ()  => linkEl.classList.remove('is-active'))
    }
  }
}
</script>

